//  城市列表, 添加景点, 修改景点, 删除景点
import { cityslist, citysadd, citysedit, citysdelete } from '../api'
import { mapGetters } from 'vuex'
export default {
  name: 'system',
  data () {
    return {
      list: { //  用户信息
        data: [], //  数据
        total: 0 //  总数
      },
      form: {}, //  表单
      currentSetSts: '', //  当前操作状态
      dialogCfg: { //  弹出框配置
        title: '', //  标题
        dialogVisible: false, //  显示隐藏弹出框
        width: '30%', //  宽度
        center: true, //  是否居中
        fullscreen: false, //  是否全屏
      },
      rules: { //  规则
        width: '100px',
        city: [{ required: true, message: '请选择城市', trigger: 'change' }],
        name: [{ required: true, message: '请输入景点名称', trigger: 'blur', type },],
        adultTicket: [{ required: true, message: '请输入成人票价格', trigger: 'blur' }, { type: 'number', message: '必须为数字值'}],
        childTicket: [{ required: true, message: '请输入儿童票价格', trigger: 'blur' }, { type: 'number', message: '必须为数字值'}],
        openingTime: [{ required: true, message: '请输入开始时间', trigger: 'blur' }, { type: 'number', message: '必须为数字值'}],
        closingTime: [{ required: true, message: '请输入结束时间', trigger: 'blur' }, { type: 'number', message: '必须为数字值'}],
      },
      para: { //  查询条件
        pageIndex: 1,
        pageSize: 10,
      }
    }
  },
  computed: {
    ...mapGetters(['getcitys']) //  城市列表
  },
  mounted() {
    this.getlist()
  },
  methods: {
    getlist () { //  查询用户列表
      cityslist(this.para).then(res => {
        console.log(res)
        let { code, msg, data, total } = res.data
        if (code !== 200) {
          this.$message.error(msg)
        } else {
          this.list.data = data
          this.list.total = total
        }
      })
    },
    handleSearch (obj, val) { //  查询数据列表 obj: 对象, val: 值
      switch (obj) {
        case 'pagination': //  分页
          this.para.pageIndex = val
          this.getlist()
          break
      }
    },
    handleSet (obj, row) { //  根据不同状态处理对应事件
      console.log(obj, row)
      this.from = row
      this.currentSetSts = obj //  设置当前操作状态
      switch (obj) {
        case 'add': //  添加
          this.dialogCfg.title = '添加'
          this.form = {}
          this.dialogCfg.dialogVisible = true
          break
        case 'edit': //  编辑
          this.dialogCfg.title = '编辑'
          this.form = row
          this.form.id = row._id
          this.dialogCfg.dialogVisible = true
          break
        case 'delete': //  删除
          this.$confirm('此操作将删除该数据是否继续?', '提示', {
            confirmButtonText: '继续',
            cancelButtonText: '取消',
            type: 'danger'
          }).then(() => {
            this.form.id = row._id
            let para = new URLSearchParams(this.form)
            citysdelete(para).then(res => {
              let { code, msg } = res
              if (code === 200) {
                this.$message.success(msg)
                this.getlist()
              } else {
                this.$message.error(msg)
              }
            })
          }).catch(() => {
            this.$message.info('已取消')
          })
          break
      }
    },
    handleSuccess () {
      switch (this.currentSetSts) {
        case 'add': //  添加
          this.$refs.form.validate((valid) => {
            if (valid) {
              let para = new URLSearchParams(this.form)
              citysadd(para).then(res => {
                console.log(res)
                let { code, msg } = res
                if (code === 200) {
                  this.$confirm('添加成功,是否继续添加?', '提示', {
                    confirmButtonText: '继续',
                    cancelButtonText: '取消',
                    type: 'primary'
                  }).then(() => {
                    this.form.name = ''
                  }).catch(() => {
                    this.$message.success(msg)
                    this.getlist()
                    this.dialogCfg.dialogVisible = false
                  })
                } else {
                  this.$message.error(msg)
                }
              })
            } else {
              this.$message.error('请填写完整')
              return false
            }
          })
          break
        case 'edit': //  编辑
          this.$refs.form.validate((valid) => {
            if (valid) {
              let para = new URLSearchParams(this.form)
              citysedit(para).then(res => {
                console.log(res)
                let { code, msg } = res
                if (code === 200) {
                  this.$message.success(msg)
                  this.getlist()
                  this.dialogCfg.dialogVisible = false
                } else {
                  this.$message.error(msg)
                }
              })
            } else {
              this.$message.error('请填写完整')
              return false
            }
          })
          break
      }
    }
  },
}
